public class Concept_of_Class_Method_Object {

	void run() {
		System.out.println("I can run");
	}
	void eat() {
		System.out.println("I am eatting");
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("My name is Shaswata");
		Concept_of_Class_Method_Object boy = new Concept_of_Class_Method_Object();
		
		boy.run();
		boy.eat();
	}

}
