
public class GreatestAmongThree {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=20, b=5, c=30;
		
		if(a>b && a>c)
			System.out.println(a+ " is greatest");
		else if(b>a && b>c)
			System.out.println(b+" is greatest");
		else
			System.out.print(c+" is greatest");
		}

}
